import { Component } from '@angular/core';
import { Cliente } from '../cliente/cliente.model';
import { ClienteService } from '../services/cliente.service';
import { ActivatedRoute } from '@angular/router';
import { NotificacaoService } from '../services/notificacao.service';
import { FormBuilder, Validators, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent { //implements OnInit
  msg: string = "";
  cliente: Cliente = new Cliente();
  clienteRecuperacao: Cliente = new Cliente();
  clienteNovo: Cliente = new Cliente();
  senha: string = "";
  confirmarSenha: string = "";
  senhasDiferentes: boolean = false;
  formCadastro = this.formBuilder.group({
    nome: ["", [
      Validators.minLength(4),
      Validators.required
    ]],
    email: ["", [
      Validators.email,
      Validators.required
    ]],
    cpf: ["", [
      Validators.max(11),
      Validators.min(11)
    ]]
  });

  constructor(
    private clienteService: ClienteService,
    private routes: ActivatedRoute,
    private notificacaoService: NotificacaoService,
    private formBuilder: FormBuilder
  ){}

  ngOnInit(){
    const clienteLogado = localStorage.getItem("cliente");
    if (clienteLogado != null) {
      window.location.href = "/minha-conta"
      
    }
  }
    
  cadastrar(): void{
    if (this.clienteNovo.senha.length > 0) {
      if (!this.senhasDiferentes) {
        try {
          this.clienteService.inserir(this.clienteNovo);
          this.msg = "Cadastro efetuado com sucesso"
          window.alert(this.msg);
          this.logar(this.clienteNovo);
        } catch (error) {
          this.msg = "Ops, parece que algo deu errado";
          this.notificacaoService.notificar(this.msg);
        }
        (document.getElementById('divCadastro') as HTMLElement).style.display = 'none';

      } else {
        this.notificacaoService.notificar("A senha e a confirmação não batem");
        return;
      }
    } else {
      this.notificacaoService.notificar("A senha não pode ser vazia");
      return;
    }
  }

  recuperarSenha(): void{
    try {
      this.clienteService.recuperarSenha(this.clienteRecuperacao).subscribe(
        (resposta: Cliente) => {
          if (resposta.nome == null) {
            //Se a resposta for nula, significa que o usuário cujo email e senha informados não foi localizado
            this.msg = "Email não localizado";
            this.notificacaoService.notificar(this.msg);
          } else {
            //Se encontrar, recebe os valores
            this.clienteRecuperacao.id = resposta.id;
            this.clienteRecuperacao.bairro = resposta.bairro;
            this.clienteRecuperacao.cep = resposta.cep;
            this.clienteRecuperacao.cidade = resposta.cidade;
            this.clienteRecuperacao.complemento = resposta.complemento;
            this.clienteRecuperacao.cpf = resposta.cpf;
            this.clienteRecuperacao.email = resposta.email;
            this.clienteRecuperacao.logradouro = resposta.logradouro;
            this.clienteRecuperacao.nome = resposta.nome;
            this.clienteRecuperacao.senha = resposta.senha;
            this.clienteRecuperacao.uf = resposta.uf;

            //Chama o novo modal para atualizar a senha
            (document.getElementById('divNovaSenha') as HTMLElement).style.display='block';
          }
        }
      );
      
    } catch (error) {
      this.msg = "Ops, parece que algo deu errado"
      this.notificacaoService.notificar(this.msg);
    }

    (document.getElementById('divEsqueciSenha') as HTMLElement).style.display = 'none';
  }

  atualizarSenha(): void{
    if (this.senha.length > 0) {
      if (!this.senhasDiferentes) {
        this.clienteRecuperacao.senha = this.senha;
      } else {
        this.notificacaoService.notificar("A senha e a confirmação não batem");
        return;
      }
    } else {
      this.notificacaoService.notificar("A senha não pode ser vazia");
      return;
    }
    try {
        this.clienteService.atualizar(this.clienteRecuperacao);
        this.cliente = this.clienteRecuperacao;
        /*
        localStorage.setItem("cliente", JSON.stringify(this.clienteRecuperacao));
        this.notificacaoService.notificar("Senha recuperada com sucesso! Bem-vindo, " + this.clienteRecuperacao.nome);
        window.location.href = "./produtos";
        */
        
        const aguardar = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));
        aguardar(1000).then(() => {
          this.logar(this.clienteRecuperacao);
        });

        alert("Senha recuperada com sucesso");
    } catch (error) {
      this.notificacaoService.notificar("Ops, parece que algo deu errado");
    }
  }

  verificarSenhas(senha: string, confirmarSenha: string): void{
    this.senhasDiferentes = senha !== confirmarSenha;
  }

  logar(login: Cliente): void{
    try {
      this.clienteService.logar(login).subscribe(
        (resposta: Cliente) => {
          if (resposta.nome == null) {
            //Se a resposta for nula, significa que o usuário cujo email e senha informados não foi localizado
            this.msg = "Email ou senha incorretos";
            this.notificacaoService.notificar(this.msg);
          } else {
            //Se encontrar o usuário, salvará no local storage
            localStorage.setItem("cliente", JSON.stringify(resposta));
            this.notificacaoService.notificar("Login efetuado com sucesso. Bem-vindo, " + resposta.nome);
            window.location.href = "./produtos";
          }
        }
      );
      
    } catch (error) {
      this.msg = "Ops, parece que algo deu errado"
      this.notificacaoService.notificar(this.msg);
    }
  }
}
<section class="w3-container w3-center w3-margin">
    <h2 class="w3-text-indigo">Identificação</h2>
    <div id="container">
        <!-- Possui cadastro -->
        <div class="w3-half w3-border-right w3-padding">
            <h3 class="w3-text-indigo">Já tenho cadastro</h3>
            <div class="cadastro">
                <input type="email" class="w3-input w3-border w3-round w3-margin" placeholder="Email" pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$" [(ngModel)]="cliente.email">
                <input type="password" class="w3-input w3-border w3-round w3-margin" placeholder="Senha" [(ngModel)]="cliente.senha">
            </div>
            <p><button class="w3-button w3-round w3-indigo w3-hover-orange" (click)="logar(cliente)">Entrar</button></p>
            <p><button class="w3-button w3-hover-none w3-text-indigo w3-hover-text-orange" onclick="document.getElementById('divEsqueciSenha').style.display='block'">Esqueci minha senha</button></p>
        </div>
    
        <!-- Não possui cadastro -->
        <div class="w3-half">
            <h3 class="w3-text-indigo">Ainda não tenho cadastro</h3>
            <button class="w3-button w3-round w3-indigo w3-hover-orange" onclick="document.getElementById('divCadastro').style.display='block'">Cadastrar</button>
        </div>
    </div>

    <!-- Modal Cadastro -->
    <div class="w3-modal" id="divCadastro">
        <div class="w3-modal-content w3-card-4 w3-animate-top w3-round">
            <header class="w3-indigo w3-container modal-header">
                <!-- Fechar -->
                <span onclick="document.getElementById('divCadastro').style.display='none'" class="w3-button w3-display-topright">&times;</span>
                <h3>Cadastrar</h3>
            </header>
            <div class="w3-container w3-padding cadastro" [formGroup]="formCadastro" >
                <input type="text" 
                    class="w3-input w3-border w3-round w3-margin" 
                    placeholder="Nome" 
                    [(ngModel)]="clienteNovo.nome">
                <input type="number" class="w3-input w3-border w3-round w3-margin" placeholder="CPF" [(ngModel)]="clienteNovo.cpf">
                <input type="email" class="w3-input w3-border w3-round w3-margin" placeholder="Email" [(ngModel)]="clienteNovo.email">
                <input type="password" class="w3-input w3-border w3-round w3-margin" [ngClass]="{'senhas-diferentes': senhasDiferentes}" placeholder="Senha" [(ngModel)]="clienteNovo.senha" (input)="verificarSenhas(clienteNovo.senha, confirmarSenha)">
                <input type="password" class="w3-input w3-border w3-round w3-margin" [ngClass]="{'senhas-diferentes': senhasDiferentes}" placeholder="Confirmar senha" [(ngModel)]="confirmarSenha" (input)="verificarSenhas(clienteNovo.senha, confirmarSenha)">
                <p class="w3-margin" style="color: red;" *ngIf="senhasDiferentes">Senha não bate com a confirmação de senha</p>

                <!-- Endereço -->
                <section class="w3-margin-left endereco">
                    <div class="w3-third">
                        <div class="w3-margin-right">
                            <input type="text" class="w3-input w3-border w3-round" placeholder="Logradouro" [(ngModel)]="clienteNovo.logradouro">
                        </div>
                    </div>
                    <div class="w3-third">
                        <div class="w3-margin-left w3-margin-right">
                            <input type="text" class="w3-input w3-border w3-round" placeholder="Complemento" [(ngModel)]="clienteNovo.complemento">
    
                        </div>
                    </div>
                    <div class="w3-third">
                        <div class="w3-margin-left">
                            <input type="text" class="w3-input w3-border w3-round w3-right" placeholder="Bairro" [(ngModel)]="clienteNovo.bairro">
                        </div>
                    </div>
                    <div class="w3-third">
                        <div class="w3-margin-right">
                            <input type="text" class="w3-input w3-border w3-round w3-margin-top" placeholder="Cidade" [(ngModel)]="clienteNovo.cidade">
                        </div>
                    </div>
                    <div class="w3-third">
                        <div class="w3-margin-left w3-margin-right">
                            <input type="text" class="w3-input w3-border w3-round w3-margin-top" placeholder="UF" [(ngModel)]="clienteNovo.uf">

                        </div>
                    </div>
                    <div class="w3-third w3-margin-bottom">
                        <div class="w3-margin-left">
                            <input type="text" class="w3-input w3-border w3-round w3-margin-top" placeholder="CEP" [(ngModel)]="clienteNovo.cep">

                        </div>
                    </div>

                </section>

                <div class="w3-padding">
                    <button class="w3-button w3-indigo w3-round w3-hover-orange" (click)="cadastrar()">Cadastrar</button>
                </div>
            </div>
        </div>
    </div>
    

    <!-- Modal Esqueci a Senha -->
    <div class="w3-modal" id="divEsqueciSenha">
        <div class="w3-modal-content w3-card-4 w3-animate-top w3-round">
            <header class="w3-indigo w3-container modal-header">
                <span onclick="document.getElementById('divEsqueciSenha').style.display='none'" class="w3-button w3-display-topright">&times;</span>
                <h3>Esqueci minha senha</h3>
            </header>
            <div class="w3-container w3-padding cadastro">
                <p>Informe seu email para recuperar a senha</p>
                <input type="email" class="w3-input w3-border w3-round w3-margin" placeholder="Email" required pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$" [(ngModel)]="clienteRecuperacao.email">
                <div class="w3-padding">
                    <button class="w3-button w3-indigo w3-round w3-hover-orange" (click)="recuperarSenha()">Recuperar senha</button>
                </div>
            </div>
        </div>
    </div>

    <!-- Modal Nova Senha -->
    <div class="w3-modal" id="divNovaSenha">
        <div class="w3-modal-content w3-card-4 w3-animate-top w3-round">
            <header class="w3-indigo w3-container modal-header">
                <span onclick="document.getElementById('divNovaSenha').style.display='none'" class="w3-button w3-display-topright">&times;</span>
                <h3>Nova senha</h3>
            </header>
            <div class="w3-container w3-padding cadastro">
                <p>Informe a sua nova senha</p>
                <input type="password" class="w3-input w3-border w3-round w3-margin" placeholder="Senha" required [(ngModel)]="senha" (input)="verificarSenhas(senha, confirmarSenha)" [ngClass]="{'senhas-diferentes': senhasDiferentes}">

                <input type="password" class="w3-input w3-border w3-round w3-margin" placeholder="Confirmar senha" required [(ngModel)]="confirmarSenha" (input)="verificarSenhas(senha, confirmarSenha)" [ngClass]="{'senhas-diferentes': senhasDiferentes}">

                <p class="w3-margin" style="color: red;" *ngIf="senhasDiferentes">Senha não bate com a confirmação de senha</p>

                <div class="w3-padding">
                    <button class="w3-button w3-indigo w3-round w3-hover-orange" (click)="atualizarSenha()">Recuperar senha</button>
                </div>
            </div>
        </div>
    </div>
</section>
