  //   this.http.post<number>("http://localhost:8081/api/pedido2", pedido).subscribe(
  //     (resposta: number) => {
  //       console.log("id pedido: " + resposta);
  //       pedido_id = resposta;
  //       console.log("var pedido_id no subscribe: " + pedido_id);
  //     }
  //   );

  //   //Insere os produtos ao pedido
  //   console.log("var pedido_id fora do subscribe: " + pedido_id);
  //   itensCarrinho.forEach(element => {
  //     let pedidoProduto: PedidoProduto = new PedidoProduto();
  //     pedidoProduto.pedido_id = pedido_id;
  //     pedidoProduto.quant = element.quantidade;
  //     pedidoProduto.produto_id = element.id;
  //     pedidoProduto.preco = element.preco;

      
  //     console.log(element.descricao);
  //     console.log(pedidoProduto.pedido_id + " quant: " + pedidoProduto.quant + " id_produto: " + pedidoProduto.produto_id);
  //     this.http.post<String>("http://localhost:8081/api/pedido-produto", pedidoProduto).subscribe();
  //   });
  // }

  public inserir(pedido: Pedido, itensCarrinho: IProdutoCarrinho[]): void {
    let pedido_id: number = 0;

    // Insere o pedido e, em seguida, insere os produtos
    this.http.post<number>("http://localhost:8081/api/pedido2", pedido).pipe(
        switchMap((resposta: number) => {
            console.log("id pedido: " + resposta);
            pedido_id = resposta;
            console.log("var pedido_id no switchMap: " + pedido_id);

            // Retorna um novo observable para a inserção dos produtos
            return this.inserirProdutosAoPedido(itensCarrinho, pedido_id);
        })
    ).subscribe(
        () => {
            console.log("Operação concluída com sucesso!");
        },
        (erro) => {
            console.error("Erro ao inserir pedido:", erro);
        }
    );
}

private inserirProdutosAoPedido(itensCarrinho: IProdutoCarrinho[], pedido_id: number): Observable<any> {
    // Retorna um observable que insere os produtos
    return from(itensCarrinho).pipe(
        switchMap((element) => {
            let pedidoProduto: PedidoProduto = new PedidoProduto();
            pedidoProduto.pedido_id = pedido_id;
            pedidoProduto.quant = element.quantidade;
            pedidoProduto.produto_id = element.id;
            pedidoProduto.preco = element.preco;

            console.log(element.descricao);
            console.log(pedidoProduto.pedido_id + " quant: " + pedidoProduto.quant + " id_produto: " + pedidoProduto.produto_id);

            // Retorna o observable para a inserção do produto
            return this.http.post<String>("http://localhost:8081/api/pedido-produto", pedidoProduto);
        })
    );
}
  
  public atualizar(pedido: Pedido){
    this.http.put<String>("http://localhost:8081/api/pedido", pedido).subscribe();
  }

  public consultar(id: number): Observable<Pedido>{
    return this.http.get<Pedido>("http://localhost:8081/api/pedido/" + id);
  }

  //Lista todos os pedidos de um determinado cliente
  public listar(cliente_id: number): Observable<Pedido[]>{
    return this.http.get<Pedido[]>("http://localhost:8081/api/pedido/lista/" + cliente_id);
  }
}
